param($path = "C:\C74"); if(!(test-path $path)){Write-Information -MessageData "Path does not exist. Creating Path..." -InformationAction Continue; New-Item -ItemType Directory -Force -Path $path | Out-Null; Write-Information -MessageData "...Complete" -InformationAction Continue}; Set-Location $path; Invoke-WebRequest -Uri https://download.sysinternals.com/files/Sysmon.zip -Outfile Sysmon.zip; Expand-Archive Sysmon.zip -Force; Set-Location $path\Sysmon; Invoke-WebRequest -Uri https://bit.ly/c74sysmonprod -Outfile sysmonconfig-export.xml; .\sysmon.exe -accepteula -i sysmonconfig-export.xml; .\sysmon.exe -accepteula -c sysmonconfig-export.xml